-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `turicode`.`imagen` (
  `id_imagen` INT NOT NULL AUTO_INCREMENT,
  `url` VARCHAR(255) NOT NULL,
  `descripcion` TEXT,
  `id_servicio` INT NOT NULL,
  PRIMARY KEY (`id_imagen`),
  INDEX `fk_imagen_servicio_idx` (`id_servicio` ASC),
  CONSTRAINT `fk_imagen_servicio`
    FOREIGN KEY (`id_servicio`)
    REFERENCES `turicode`.`servicio` (`id_servicio`)
    ON DELETE CASCADE
    ON UPDATE CASCADE
)
ENGINE = InnoDB
DEFAULT CHARACTER SET = utf8mb3;

